    private void TextDisplay1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_TextDisplay1KeyTyped
        char c = evt.getKeyChar();
        if(!(Character.isDigit(c) || (c == KeyEvent.VK_BACK_SPACE) || (c == KeyEvent.VK_DELETE)))
        {
            getToolkit().beep();
            evt.consume();
        }
    }//GEN-LAST:event_TextDisplay1KeyTyped

    private void BtnIsEqualsToActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnIsEqualsToActionPerformed
        String ans;
        secondnum = Double.parseDouble(TextDisplay1.getText());
        if (operation == "+")
        {
            result = firstnum + secondnum;
            ans =  String.format("%.2f", result);
            TextDisplay1.setText(ans);
        }
        
        else if (operation == "-")
        {
            result = firstnum - secondnum;
            ans =  String.format("%.2f", result);
            TextDisplay1.setText(ans);
        }
        
        else if (operation == "*")
        {
            result = firstnum * secondnum;
            ans =  String.format("%.2f", result);
            TextDisplay1.setText(ans);
        }
        
        else if (operation == "/")
        {
            result = firstnum / secondnum;
            ans =  String.format("%.2f", result);
            TextDisplay1.setText(ans);
        }
        
        else if (operation == "%")
        {
            result = firstnum % secondnum;
            ans =  String.format("%.2f", result);
            TextDisplay1.setText(ans);
        }
        
    }//GEN-LAST:event_BtnIsEqualsToActionPerformed

