    private void BtnNfactActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnNfactActionPerformed
        double ops = Double.parseDouble(TextDisplay1.getText());
        double fact = 1;
        while(ops != 0)
        {
            fact = fact * ops;
            ops--;
        }
        TextDisplay1.setText("");
        TextDisplay1.setText(TextDisplay1.getText()+fact);
    }//GEN-LAST:event_BtnNfactActionPerformed

